        private static int CalculateFloor(string input)
        {
            int floor = 0;

            for (int i = 0; i < input.Length; i++)
            {
                if (input [i] == '(')
                {
                    floor++;
                }
                else if (input [i] == ')')
                {
                    floor--;
                }
                if (floor == -1)
                {
                    Console.WriteLine("Floor -1 reached at: " + (i + 1).ToString());
                }
            }
            return floor;
        }
